DROP TABLE IF EXISTS "auth_group";
CREATE TABLE "auth_group" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(80) NOT NULL UNIQUE);
DROP TABLE IF EXISTS "auth_group_permissions";
CREATE TABLE "auth_group_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "group_id" integer NOT NULL REFERENCES "auth_group" ("id"), "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id"), UNIQUE ("group_id", "permission_id"));
DROP TABLE IF EXISTS "auth_permission";
CREATE TABLE "auth_permission" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(50) NOT NULL, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id"), "codename" varchar(100) NOT NULL, UNIQUE ("content_type_id", "codename"));
DROP TABLE IF EXISTS "auth_user";
CREATE TABLE "auth_user" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NOT NULL, "is_superuser" bool NOT NULL, "username" varchar(30) NOT NULL UNIQUE, "first_name" varchar(30) NOT NULL, "last_name" varchar(30) NOT NULL, "email" varchar(75) NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL, "date_joined" datetime NOT NULL);
DROP TABLE IF EXISTS "auth_user_groups";
CREATE TABLE "auth_user_groups" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id"), "group_id" integer NOT NULL REFERENCES "auth_group" ("id"), UNIQUE ("user_id", "group_id"));
DROP TABLE IF EXISTS "auth_user_user_permissions";
CREATE TABLE "auth_user_user_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id"), "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id"), UNIQUE ("user_id", "permission_id"));
DROP TABLE IF EXISTS "django_admin_log";
CREATE TABLE "django_admin_log" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "action_time" datetime NOT NULL, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "action_flag" smallint unsigned NOT NULL, "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id"), "user_id" integer NOT NULL REFERENCES "auth_user" ("id"));
DROP TABLE IF EXISTS "django_content_type";
CREATE TABLE "django_content_type" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "app_label" varchar(100) NOT NULL, "model" varchar(100) NOT NULL, UNIQUE ("app_label", "model"));
DROP TABLE IF EXISTS "django_migrations";
CREATE TABLE "django_migrations" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app" varchar(255) NOT NULL, "name" varchar(255) NOT NULL, "applied" datetime NOT NULL);
DROP TABLE IF EXISTS "django_session";
CREATE TABLE "django_session" ("session_key" varchar(40) NOT NULL PRIMARY KEY, "session_data" text NOT NULL, "expire_date" datetime NOT NULL);
DROP TABLE IF EXISTS "parts_part";
CREATE TABLE "parts_part" ("serial_number" integer NOT NULL PRIMARY KEY, "part_number" integer NOT NULL, "price" decimal NOT NULL, "quantity" integer NOT NULL, "location" varchar(200) NOT NULL);
DROP TABLE IF EXISTS "repairs_address";
CREATE TABLE "repairs_address" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "street" varchar(200) NOT NULL, "city" varchar(200) NOT NULL, "state" varchar(200) NOT NULL, "zip" integer NOT NULL, "phone" varchar(12) NOT NULL, "contact_name" varchar(200) NOT NULL);
DROP TABLE IF EXISTS "repairs_customer";
CREATE TABLE "repairs_customer" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "email" varchar(75) NOT NULL, "billing_address_id" integer NOT NULL REFERENCES "repairs_address" ("id"), "street_address_id" integer NOT NULL REFERENCES "repairs_address" ("id"));
DROP TABLE IF EXISTS "repairs_machine";
CREATE TABLE "repairs_machine" ("serial_number" integer NOT NULL PRIMARY KEY, "model" varchar(200) NOT NULL, "manufacture_date" date NOT NULL, "passwd" varchar(200) NOT NULL, "pulse_count" integer NOT NULL, "software_version" real NOT NULL);
DROP TABLE IF EXISTS "repairs_part";
CREATE TABLE "repairs_part" ("serial_number" integer NOT NULL PRIMARY KEY, "part_number" integer NOT NULL, "quantity" integer NOT NULL, "price" decimal NOT NULL);
DROP TABLE IF EXISTS "repairs_serviceengineer";
CREATE TABLE "repairs_serviceengineer" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "first_name" varchar(200) NOT NULL, "last_name" varchar(200) NOT NULL);
DROP TABLE IF EXISTS "repairs_servicelog";
CREATE TABLE "repairs_servicelog" ("rma_number" integer NOT NULL PRIMARY KEY, "date" date NOT NULL, "condition" text NOT NULL, "correction" text NOT NULL, "notes" text NOT NULL, "payment_category" varchar(20) NOT NULL, "service_category" varchar(20) NOT NULL, "customer_id" integer NOT NULL REFERENCES "repairs_customer" ("id"), "engineer_id" integer NOT NULL REFERENCES "repairs_serviceengineer" ("id"), "machine_id" integer NOT NULL REFERENCES "repairs_machine" ("serial_number"), "parts_charge" decimal NOT NULL, "purchase_order" integer NOT NULL, "total_charge" decimal NULL, "zone_charge" decimal NOT NULL);
